import styled from 'styled-components'
import breakpoints from '../../particles/breakpoints'

const isDisplayProperty = props => {

    switch(props.isEqualFlex){
        case "flex":
            return "flex";
            break;
        case "block":
            return "block";
            break;
        case "inline-block":
            return "inline-block";
            break;
        default: 
            return "flex"
            break
    }
}

const masked = props => {

    if(props.masked){
        return "hidden";
    }else{
        return "visible";
    }
}

export const StyledView = styled.div`
    display: ${props => isDisplayProperty(props)};
    flex-direction: ${props => props.direction};
    flex: ${props => props.flexValue ? '1': 'none'};
    justify-content: ${props => props.justify};
    align-items: ${props => !props.align ? 'none' : props.align};
    width: ${props => props.width};
    max-width: ${props => !props.maxWidth ? undefined : props.maxWidth};
    height: ${props => props.height};
    background-color: ${props => props.backgroundColor};
    border-radius: ${props => props.radius};
    box-shadow: ${props => props.isBoxShadow}; //10px 20px 50px rgba(149, 163, 180, 0.1);
    overflow: ${props => props.overflow};
    position: ${props => props.position};
    left: ${props => props.left};
    top: ${props => props.top};
    bottom: ${props => props.bottom};
    padding: ${props => props.padding};
    margin: ${props => props.margin};
    box-sizing: border-box;

    &.half {
        width: calc(50% - 40px);
        margin: 0 5px 10px 0;

        &:nth-child(2n) {
          margin: 0 0 10px 5px;
        }
    }

    &.large {
        width: 100%;
        margin: 0 0 50px 0;
        display: flex;
        justify-content: space-between;
    }

    &.semi-half {
        width: calc(70% - 50px);
        margin: 0 5px 10px 0;

        &:nth-child(2n) {
          width:30%;
          margin: 0 0 10px 5px;
        }
    }

    &.grid-item {
        height: auto;
        flex-basis: 33.33%;
        -ms-flex: auto;
        width: 259px;
        position: relative;
        padding: 10px;
        box-sizing: border-box;
        margin-bottom:50px;
    }

      @media ${breakpoints.laptopL} {
        &.grid-item {
          flex-basis: 33.33%;
        }
      }

      @media ${breakpoints.laptop} {
        .grid-item {
          flex-basis: 33.33%;
        }
      }

      @media ${breakpoints.tablet} {
        &.grid-item {
          flex-basis: 50%;
        }
      }



    @media ${breakpoints.mobileL} {
        width: ${props => !props.isEqualWidth ? undefined : props.isEqualWidth} !important;
        height: ${props => !props.isEqualHeight ? undefined : props.isEqualHeight} !important;
        background-color: ${props => props.isEqualBackground};
        flex-direction: ${props => props.isEqualDirection};

        &.grid-item {
          flex-basis: 100%;
        }
    }
`

export default StyledView
