import React, { useEffect, useState } from 'react'
import { BlogLayout } from "../../components/templates/layouts";
import { Container, View, Text, Image} from '../../components/atoms'

import './stories.scss'
import Link from 'next/link';
import { useRouter } from 'next/dist/client/router';
import { useSelector } from 'react-redux';
import * as Markdown from 'react-markdown'
import { documentToHtmlString } from '@contentful/rich-text-html-renderer';
import { documentToReactComponents } from '@contentful/rich-text-react-renderer';
import { BLOCKS, MARKS } from '@contentful/rich-text-types';

const Bold = ({ children }) => <p className="bold">{children}</p>;

const Texts = ({ children }) => <p className="align-center">{children}</p>;
const options = {
    renderMark: {
      [MARKS.BOLD]: text => <Bold>{text}</Bold>,
    },
    renderNode: {
      [BLOCKS.PARAGRAPH]: (node, children) => <Texts>{children}</Texts>,
    },
    renderText: text => text.replace('!', '?'),
  };
const content = documentToReactComponents(story.body, options)
const Story = (props) => {
    const router = useRouter()
    const [slug, setSlug] = useState('')
    const story = useSelector(state => state.stories.story.fields)



    useEffect(() => {
        let param = router.query.slug
        setSlug(param)
        console.log("STATE :: ",story)
    })
    return (
        <BlogLayout>
            <Container className="story-container">
                <View className="story-content" direction="column">
                    <Link href="/stories">
                        <a>
                            <View className="story-close-btn">
                                <Image src="/static/illustrations/close-dark.svg"/>
                            </View>
                        </a>
                    </Link>
                    <View className="story-content-header">
                        <Text className="title" family="quicksand">{story.title}</Text>
                        <Text className="subtitle" family="quicksand">{story.subtitle}</Text>
                        <Image src={`https:${story.heroImage.fields.file.url}`}/>
                    </View>
                    <View className="story-content-body">
                        <Markdown source={content} escapeHtml={false}/>
                    </View>
                </View>
            </Container>
        </BlogLayout>
    )
}

export default Story
