import styled from 'styled-components'
import breakpoints from '../../particles/breakpoints'

const isBackgroundColor = props => {
  if(!props.variant) return "#3838F8"

  let color;
  switch(props.variant){
    case "primary":
      color = "#3838F8"
      break;
    case "secondary":
      color = "#EDEDED"
      break;
    default:
      color = "#001CF9"
      break;
  }

  return color;
}


const isHover = props => {
  let color;
  switch(props.variant){
    case "primary":
      color = "#F7FBFF"
      break;
    case "secondary":
      color = "#001CF9"
      break;
    default:
      color = "#001CF9"
      break;
  }
  return color;
}


const isCursor = props => {
  if(props.variant == "disabled"){
    return "not-allowed"
  }else{
    return "pointer"
  }
}

const isColor = props => {
  if(props.variant == "secondary"){
    return "#1E2127"
  }else{
    return "#ffffff"
  }
}


const isPointer = props => {
  if(props.variant == "disabled"){
    return "none"
  }else{
    return "all"
  }
}

const border = props => {

  if(!props.border) return "none"

  switch (props.border) {
    case 'primary':
      return "#3838F8"
      break;
    case 'secondary':
      return "#EDEDED"
    default:
      return "none"
      break;
  }
}

export const StyledButton = styled.button`
    position: relative;
    display: inline-block;
    padding: 8px 12px 8px 12px;
    width: ${props => props.width};
    height: ${props => props.height};
    min-width: 120px;
    border:  ${props => !props.border ? 'none' : '1px solid '+border(props)};
    border-radius: 5px;
    background-color: ${props => isBackgroundColor(props)};
    cursor: ${props => isCursor(props)};
    pointer-events: ${props => isPointer(props)};
    text-decoration: none;
    transition: all 0.8s ease;
    white-space: nowrap;
    font-family: 'Montserrat', sans-serif;
    font-style: normal;
    font-size: 14px;
    font-weight: 500;
    line-height: 17px;
    text-align: center;
    color: ${props => isColor(props)};
    &:hover{
        color: ${props => !props.isFocus ? '#ffffff' : '#001CF9'};

    box-shadow: 2px 10px 15px rgba(30, 33, 39, 0.10);


      }
    &:focus{
      outline: none;
    }

    &:active {
      outline: none;
    }

    @media ${breakpoints.mobileL} {
        width: ${props => props.isEqualWidth} !important;
        height: ${props => props.isEqualHeight} !important;
    }
`

export const StyledLinkButton = styled(StyledButton).attrs({
  as: "a"
})`
  text-deocration: none;
`;

export const SyledButtonIcon = styled.span`
  position: ${props => props.iconPosition};
  right: ${props => props.iconRight};
  top: ${props => props.iconTop};
  left: ${props => props.iconLeft};
  bottom: ${props => props.iconBottom};
`

export default StyledButton
