import React from 'react'
import {View, Badge, Text} from '../../../atoms'

const CardText = ({title, subtitle, variant, color, badgeTitle, props}) => {

    return (
        <View {...props} direction="column" maxWidth="230px" className="grid-item">
            <View direction="column" flexValue="1">
                <View>
                    <Text size="16" weight="semi-bold" margin="0">
                        {title}
                    </Text>
                </View>
                <View margin="0 10px 0 0">
                    <Text size="12">
                        {subtitle}
                    </Text>
                </View>
            </View>
            <Badge 
            title={badgeTitle} 
            backgroundColor={variant}
            color={color}
            marginTop="30px"/>
        </View>
    )
}

export default CardText
