import React from 'react'
import {Rows, Image, View} from '../../../atoms'
import {CardText} from '../../../molecules'


const ArticleText = ({items, image, props}) => {

    if(image){
        return (
            <Rows {...props}>
                <View align="center">
                    <Image src={image} width="90%" position="relative" top="-20px"/>
                </View>
                {
                    items.map(item => (
                        <CardText
                            key={item.id}
                            title={item.title}
                            subtitle={item.subtitle}
                            variant={item.badge.background}
                            color={item.badge.color}
                            badgeTitle={item.badge.title}
                        />
                    ))
                }
            </Rows>
        )
    }
    return (
        <Rows {...props}>
            {
                items.map(item => (
                    <CardText
                        key={item.id}
                        title={item.title}
                        subtitle={item.subtitle}
                        variant={item.badge.background}
                        color={item.badge.color}
                        badgeTitle={item.badge.title}
                    />
                ))
            }
        </Rows>
    )
}

export default ArticleText
