import React, {Component} from 'react'
import Layout from "../../components/templates/layouts";
import {Hero} from '../../components/organisms'
import ImageHero from '../../assets/illustrations/daily-stories.svg'
import {SliderHero} from '../../components/organisms'
import Resizable from '../../components/organisms/articles/sliderHero/slide.jsx'
import Carousel from "react-multi-carousel";
import "react-multi-carousel/lib/styles.css";
import {View} from '../../components/atoms';


class Stories extends Component {
    state = {
        display: true,
        width: 600
      };
  render() {
    const settings = {
        dots: true,
        infinite: true,
        speed: 500,
        slidesToShow: 3,
        slidesToScroll: 1
      };
    return ( <> <Layout title="Stories">
      <Hero
        title="Stories Resources"
        subtitle="The more that you read the more things you will know."
        img={ImageHero}/> {/* <SliderHero/>     */}
      <View width="800px">
        <Slider {...settings}>
          <div>
            <h3>1</h3>
          </div>
          <div>
            <h3>2</h3>
          </div>
          <div>
            <h3>3</h3>
          </div>
          <div>
            <h3>4</h3>
          </div>
          <div>
            <h3>5</h3>
          </div>
          <div>
            <h3>6</h3>
          </div>
        </Slider>
      </View>
    </Layout> < />
        )
    }
}

export default Stories;
