import { SET_STORY } from './type'
import Router from 'next/router'

const setStory = (story) => {
    return (dispatch) => {
      dispatch({type: SET_STORY, payload: story});
      Router.push(`/stories/${story.fields.slug}`)
      console.log("PAYLOAD :: ", story.fields.slug)
    };
  };

  export default {
      setStory
  }
